  GlobalKey<OiState> oiStateKey = GlobalKey<OiState>();

  //* breakpoint de telas...
  _ajustarVisualizcao(double larguraTela) {
    // oiState.clean();
    oiStateKey.currentState?.clean();
    int colunas = 2;
    if (larguraTela <= 600) {
      colunas = 2;
    } else if (larguraTela <= 960) {
      colunas = 4;
    } else {
      colunas = 6;
    }
    return colunas;
  }
  

  @override
  Widget build(BuildContext context) {
    return LayoutBuilder(
      builder: (context, constraint) {
        var largura = constraint.maxWidth;
        
        return Scaffold(
          body: Padding(
            padding: EdgeInsets.all(16),
            child: GridView.count(
              crossAxisCount: _ajustarVisualizcao(largura),
              crossAxisSpacing: 8,
              mainAxisSpacing: 8,
              children: [
                Oi(key: oiStateKey,),
                Noticias(
